import React, { useState,useEffect } from 'react'
import CheckoutProduct from './CheckoutProduct'
import Subtotal from './Subtotal'
import { useContext } from "react";
import CommerceContext from "./context/commerce-context";



function Checkout() {




  const { basket, removeFromCart } = useContext(CommerceContext)
  const [basketState, setBasketState] = useState(basket)
  console.log(basketState)

  useEffect(() => {
localStorage.setItem('myCat', basketState)
  }, [basketState]);

   const sum = basket.reduce((total, item) => {
     return total + (item.pricing * item.qty);

  }, 0);


    return (
        <div className="w-[90%] md:flex md:justify-between mx-auto"> 



        <div className='md:w-[60%] mb-3  w-full'>

            <h2 className="text-xl p-4 font-semibold">
              Your Cart ({basket.length})
            </h2>

          {

            basket.map(basketItem => <CheckoutProduct 
            key={basketItem.id}
            id={basketItem.id}
            title={basketItem.title}
            pricing={basketItem.pricing}
            qty={basketItem.qty}
            image={basketItem.image}
            size={basketItem.size}
            clickToRemove={() =>  removeFromCart(basketItem.id)}
          />

            )}

          </div>

        <div className='md:w-[30%] h-[200px] w-full md:fixed md:right-20 md:-z-10 bg-slate-100 shadow-2xl' >
          <Subtotal total={sum}/>
        </div>
        </div>
    )
}

export default Checkout
