import React from 'react'
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";

export default function HeroProducts() {
    const ImageSlider = ({ heroItems }) => {

        const settings = {
            infinite: true,
            dots: true,
            slidesToShow: 1,
            slidesToScroll: 1,

            autoplay: true,
            autoplaySpeed: 2000,

        };

        return (
            <>
                <div className="tag">
                    <h1>Image Gallery</h1>
                </div>
                <div className="imgslider">
                    <Slider {...settings}>
                        {heroItems.map((item) => (

                               <div key={item.id}>
                                <img src={item.src} alt={item.alt} />
                                <div className='relative top-[-100px] w-[50px] flex h-[20px] ml-4 '>
                                    <div className='bg-blue-900'><h1 className='p-4'>NEW ARRIVAL</h1></div>
                                    <div className='bg-slate-800'><h1 className='p-4'>SHOP NOW</h1></div>



                                </div>
                            </div>
                        ))}
                    </Slider>
                </div>
            </>
        )
    }
}
