import React from 'react'
import './Checkout.css'
import CheckoutProduct from './CheckoutProduct'
import Subtotal from './Subtotal'
import { useContext } from "react";
import CommerceContext from "./context/commerce-context";


function Checkout() {
  const { basket, removeFromCart } = useContext(CommerceContext)

   const sum = basket.reduce((total, item) => {
    return total + item.pricing;
  }, 0);


  function showalert(alert) {
    alert(alert)
}
    return (
        <div className="Checkout"> 
         <div className="checkout__left">
          <div className="checkout__ad">

            </div>
            <div>
              <h2 className="checkout__title">
                Your Product basket
              </h2>
              </div>
          {basket.map(basketItem => <CheckoutProduct 
            key={basketItem.id}
            title={basketItem.title}
            pricing={basketItem.pricing}
            image={basketItem.image}
            clickToRemove={() => showalert(alert)}
            />)}
          </div>

        <div className="checkout__right">
          <Subtotal total={sum}/>
        </div>
        </div>
    )
}

export default Checkout
