import React from 'react'

import CurrencyFormat from 'react-currency-format'
import { useContext } from 'react'
import CommerceContext from './context/commerce-context'



function Subtotal({ total }) {
const {basket} = useContext(CommerceContext)

    return (
        <div className="p-5 bg-slate-100 shadow-2xl">
            <div className='mx-auto'><CurrencyFormat 
                renderText={(value) => ( <>
                        <p>
                        Subtotal ({basket.length } items): <strong>${total}</strong></p>
                      </> 
                )}
                decimalScale={2}
                value={0}
                displayType={"text"}
                thousandSeparator={true}


            />
            </div>
            <div className=''>
                <button>Checkout</button>
            </div>
        </div>
    )
}

export default Subtotal
