import React from 'react'
import './CheckoutProduct.css'
import {useContext} from 'react'
import CommerceContext from './context/commerce-context'
import ReactImageZoom from 'react-image-zoom';

function CheckoutProduct({ key, id,title, image, pricing, clickToRemove,qty  }) {
    const props = { width: 300, height: 300, zoomWidth: 300, img: image, zoomPosition: "original" };
    const { incQty, decQty } = useContext(CommerceContext)

    function increment() {
        if (qty >= 1) {
            incQty(id)
        }
    }

    function decrement() {
        if (qty > 1) {
            decQty(id)
        }
        else {
            alert ("Quantity of product can not be less that 1")
        }
    }

    return (


            <div className='md:w-[60%] w-full md:flex justify-between shadow-2xl p-6'>



            <div className='p-3'> <ReactImageZoom {...props} /></div>
                    <div>
                <h1 className='text-xl px-4 py-2 font-bold'>{title}</h1>
                <h1 className='text-sm px-4 py-2 font-semibold'> Price:  ${pricing * qty} </h1>
                <div className='flex justify-start p-4' ><h1 className='text-sm font-semibold'>Quantity:  </h1> <button className=" px-[10px] py-[5px] bg-blue-500" onClick={decrement}>-</button>
                <span className="counter--count">{qty}</span>
                    <button className="p-2 bg-blue-500" onClick={increment}>+</button>
                </div>
                <div className='p-4'><button onClick={clickToRemove}>Remove from Basket</button></div>

         </div>







        </div>
    )

}
export default CheckoutProduct
