import React from 'react'
import './CheckoutProduct.css'
import {useState} from 'react'


function CheckoutProduct({ key, title, image, pricing, clickToRemove, value, onCh }) {
    const [count,SetCount] = useState(1)

    const incCount= () => {

        if (count >= 1) {
            SetCount(count + 1)
        }
        else {
            count
        }
    }



    return (


            <table>


                <tbody>
                    <tr>
                    <td data-label="Account"><img src={image} alt="" width="50px"/></td>
                    <td data-label="Due Date">{title}</td>
                    <td data-label="Amount">$ {pricing}</td>
                    <td data-label="Period">  <button onClick={clickToRemove}>Remove from Basket</button></td>
                    <td data-label="Period">  <button onClick={incCount}>+</button>{count} <button onClick={() => SetCount(count + 1)}>-</button>

                    </td>
                    </tr>

                </tbody>

        </table>
    )

}
export default CheckoutProduct
