import React from 'react'
import './CheckoutProduct.css'
import {useContext} from 'react'
import CommerceContext from './context/commerce-context'


function CheckoutProduct({ key, id,title, image, pricing, clickToRemove,qty  }) {

    const { incQty, decQty } = useContext(CommerceContext)

    function increment() {
        if (qty >= 1) {
            incQty(id)
        }
    }

    function decrement() {
        if (qty > 1) {
            decQty(id)
        }
        else {
            alert ("Quantity of product can not be less that 1")
        }
    }

    return (


            <div className='md:w-[70%] w-full md:flex justify-between'>


                <tbody>
                    <tr>
                    <td data-label="Photo"><img src={image} alt="" width="50px"/></td>
                    <td data-label="Description">{title}</td>
                    <td data-label="Amount">$ {pricing * qty}</td>
                    <td data-label="Action">  <button onClick={clickToRemove}>Remove from Basket</button></td>
                    <td data-label="Quantity"> <div className="counter">
                        <button className="counter--plus" onClick={decrement}>-</button>
                        <span className="counter--count">{qty}</span>
                        <button className="counter--minus" onClick={increment}>+</button>

                    </div>
                    </td>
                    </tr>

                </tbody>

        </div>
    )

}
export default CheckoutProduct
