import React from "react";
import { ADD_T0_CART, REMOVE_FROM_CART, INC_QTY, DEC_QTY, EMPTY_CART, } from "./commerce-action";
import { useReducer } from "react";
import CommerceContext from "./commerce-context";
import {commerceReducer} from './commerce-reducer'
import { useEffect } from "react";
import { signInWithPopup } from 'firebase/auth'
import {auth,provider} from '../firebaseConfig'

const storageKey = "localCart";

const InitialState = {
    basket: [], //id, title,price,image
}


// add to cart



export default function CommerceState(props) {
    const [state, dispatch] = useReducer(commerceReducer, InitialState, (initial) => JSON.parse(localStorage.getItem(storageKey)) || initial)

const signInWithGoogle = () => {
        signInWithPopup(auth, provider)
            .then((result) => {
                const name = result.user.displayName
                const email = result.user.email
                const userPhotoUrl = result.user.photoURL

                localStorage.setItem("name", name)
                localStorage.setItem("email", email)
                localStorage.setItem("userPhoto", userPhotoUrl)
                console.log(result)
            }).catch((error) => {
                console.log(error)
            })
    }  

    const incQty = (itemID) => {
        dispatch({
            type: INC_QTY,
            payload: itemID
        })
    }

    useEffect(() => {
        localStorage.setItem(storageKey, JSON.stringify(state));

    }, [state]);

    const totalQty = state.basket.reduce((totalQty, item) => {
        return totalQty + (item.qty)
    }, 0)
    console.log(totalQty)


    const decQty = (itemID) => {
        dispatch({
            type: DEC_QTY,
            payload:itemID

        })
    }
    const addToCart = (item) => {
        dispatch({
            type: ADD_T0_CART,
            payload: item

        })
    }

    const removeFromCart = (itemId) => {
        dispatch({
            type: REMOVE_FROM_CART,
            payload: itemId
        })
    }

    const emptyCart = () => {
        dispatch({
            type:EMPTY_CART
        })
    }

  return (

      <CommerceContext.Provider value={{
          basket: state.basket,
          addToCart,
          removeFromCart,
          incQty,
          decQty,
          emptyCart,
          totalQty 
      }}>
          {props.children}
      </CommerceContext.Provider>



  )
}
