import React, { useState,useContext } from 'react'
import { useEffect } from 'react';
import { toast } from 'react-toastify';
import { useParams } from 'react-router-dom'
import productItems from "./productApi";
import ReactImageZoom from 'react-image-zoom';
import CommerceContext from './context/commerce-context';
import hottestApi from './hottestApi'
import Hottest from './hottest';
import Slide from 'react-reveal/Slide';
export default function ItemDisplay() {
  const { id } = useParams()
  const {addToCart,basket,removeFromCart} = useContext(CommerceContext)
  const [cartIcon, setCartIcon] = useState(false)
  const switchCartButton = () => setCartIcon(!cartIcon)





  const handleAddToCart = () => {
    addToCart(newItem)
    toast("Product has been added to your cart")
  }

  const handleRemoveFromCart = () => {
    removeFromCart(id)
    toast("Product has been removed from your cart")
  }


const itemIndex = productItems.find((item) => item.id == id);
 console.log(itemIndex.title)
  const props = { width: 500, height: 400, zoomWidth: 500, img: itemIndex.image, zoomPosition:"original" };

  const newItem = {
    id:itemIndex.id,
    title:itemIndex.title,
    pricing:itemIndex.pricing,
    qty: itemIndex.qty,
    image:itemIndex.image,
    alt: itemIndex.alt

  }
  return (
    <div className='w-[90%] h-full mx-auto my-9 '>
      <div className='md:flex md;justify-between w-full h-full'>
        <div className='md:w-[50%] w-full h-full'>
          <ReactImageZoom {...props} />


        </div>
        <div className='h-full w-full md:w-[50%]'>
          <h1 className='text-center mt-10 p-3 text-2xl font-bold'>{itemIndex.title}</h1>
          <h1 className='text-center text-sm'>{itemIndex.desc}</h1>
          <h1 className='text-center mt-10 text-2xl font-bold'>${itemIndex.pricing}</h1>
          <p className='text-[10px] text-center'>(Free delivery within NG,US,UK)</p>
          <div className='w-[30%] mx-auto py-5'> {!cartIcon ? <button className='w-full' onClick={() => { handleAddToCart(); switchCartButton(); }}>Add To Cart</button>
            : <button onClick={() => { handleRemoveFromCart(); switchCartButton(); }} className='w-full'>Take off Cart</button>} </div>



        </div>


      </div>

      <div class=" md:flex md:justify-between w-full mb-6">
        <Slide right>
          <div class="bg-white w-full md:w-[200px] h-ful pl-3 mx-auto mb-8">
            <h1 className=' text-center text-[30px] p-6 font-bold text-blue-400'> LORDS OF THE LEATHER</h1>
            <div className='px-6 hidden'><button className='w-full'>More!</button></div>

          </div>
          {hottestApi.slice(0, 3).map(({
            id,
            title,
            image,
            pricing,
            alt,
            qty,
          }) => (
            <Hottest
              key={id}
              id={id}
              title={title}
              pricing={pricing}
              qty={qty}
              image={image}
              alt={alt}
            />
          ))}
        </Slide></div>


    </div>
  )
}
