import React from 'react'
import Product from './Product'
import productItems from "./productApi";
import Hottest from './hottest'
import hottestApi from './hottestApi'
import Slide from 'react-reveal/Slide';
import KingsCollection from './kingsCollection';
import kingsData from './kingscollectiondata';
function Home() {

    return (


        <div className="className='md:w-[90%] mx-auto w-[90%] md:h-[300px] h-[100px] mb-8">

            <div class=" md:flex md:justify-between w-full">
                <Slide right>
                <div class="bg-white w-full md:w-[200px] h-ful pl-3 mx-auto">
                    <h1 className=' text-center text-[30px] p-6 font-bold text-blue-400'> HOTTEST DEALS OF THE DAY</h1> </div>
                    {hottestApi.slice(0, 3).map(({
                        id,
                        title,
                        image,
                        pricing,
                        alt,
                        qty,
                    }) => (
                        <Hottest
                            key={id}
                            id={id}
                            title={title}
                            pricing={pricing}
                            qty={qty}
                            image={image}
                            alt ={alt}
                        />
                    ))}
                </Slide>
                </div>

            <div className="className='md:w-[90%] mx-auto w-[90%] md:h-[400px] h-[200px] mb-8">
                <div>
                <KingsCollection
                    key={id}
                    id={id}
                    title={title}
                    pricing={pricing}
                    qty={qty}
                    image={image}
                    alt={alt}
                    />
                </div>
            </div>
       </div>

    )
}

export default Home
