import { ADD_T0_CART, DEC_QTY, INC_QTY, REMOVE_FROM_CART,} from "./commerce-action";




export const commerceReducer = (state,action) => {
    switch (action.type) {
        case ADD_T0_CART:
            return {
                ...state, basket: [...state.basket, action.payload]
            }
        case REMOVE_FROM_CART:


            return {
                ...state,
                basket: state.basket.filter( item => item.id !== action.payload)
            }

        case INC_QTY:
            const index = state.basket.map(item => item.id)
            return alert(index)

        case DEC_QTY:
            return {
                ...state,
                basket: state.basket.filter(item => item.qty - 1)
            }


        default:
            return state
    }

}

