import { ADD_T0_CART, REMOVE_FROM_CART } from "./commerce-action";


const sum = state.basket.reduce((accumulator, object) => {
    return accumulator + object.price;
}, 0);

export const commerceReducer = (state,action) => {
    switch (action.type) {
        case ADD_T0_CART:
            return {
                ...state, basket: [...state.basket, action.payload]
            }
        case REMOVE_FROM_CART:
            return {
                ...state,
                basket: state.basket.filter(item => item.id !== action.payload)
            }
        case AMOUNT:
            return {
                ...state,
                basket: sum()
            }
        default:
            return state
    }

}
