import React,{useState} from 'react'
import productItems from './productApi'
import { Icon } from '@iconify/react'
import SearchResult from  './SearchResult'
function SearchComponent() {
    const [searchField, setSearchField] = useState("");
    const searchValue = productItems.filter((item) => {
        item.title.toLowerCase().includes(searchField.toLowerCase)
    })
    console.log(searchField)
    return (
      <>

      <div className='hidden md:flex md:w-full h-full'>
              <input type='text' className='md:w-full text-[13px] bg-blue-200 p-4  rounded-full outline-none md:rounded-full 
                        focus:border-[0px] md:box-border box-border md:border-[0px] 
                        h-full' placeholder='Search...' value={searchField} onChange={(event) =>setSearchField(event.target.value)} />
      </div>
            <div className='md:bg-blue-200 rounded-full w-17 h-full hidden md:flex'><Icon  icon="bxs:search" className='md:w-10 md:h-[full] w-7 h-[30px] text-blue-800 mx-auto my-auto' /></div>

            <div className='md:grid md:grid-cols-4 w-full p-4'>
                <div>

                    {productItems.filter(searchItem => {
                        if (searchField == "") {
                      return searchItem
                        } else if (
                            searchItem.title.toLowerCase().includes(searchField.toLowerCase())
                        ) {
                            return searchItem
                        }
                    }).map((item,key) => {
                        <div className='bg-slate-200 w-[300px] h-[300px]' key={item.key}>
                            <h1>{item.title}</h1>
                            <div><img src={item.image} alt={item.alt} /></div>
                            </div>
                   })
                    }
              </div>
            </div>

        </>
    )


}

export default SearchComponent
