import React from 'react'
import { toast } from 'react-toastify';
import CommerceContext from './context/commerce-context';
import { useState,useContext } from 'react';

export default function KingsCollection({ id, title, pricing, alt, image, desc, qty }) {
    const { addToCart, removeFromCart } = useContext(CommerceContext)
    const [cartIcon, setCartIcon] = useState(false)

    const switchCartButton = () => setCartIcon(!cartIcon)

    const newItem = {
        id,
        title,
        pricing,
        image,
        qty
    }
    return (

        <div className="md:w-[100%] mx-auto w-[100%] md:h-[400px]">

            <div className='md:flex md:justify-between items-start '>
                <div className='md:w-[50%] w-[100%] h-[400px] bg-slate-900 kings mb-[-100px]'><img src={image} alt={alt}
                     /></div>
                <div className='md:w-[50%] w-[100%] h-[400px] bg-blue-400'>
                    <h1 className='text-white font-bold text-2xl text-center p-8'>KINGS OF THE STREET COLLECTION</h1><hr/>
                    <h1 className='text-3xl font-semibold text-center px-10 mt-[50px]'>{title}</h1>
                    <p className='text-sm  text-center px-4'>{desc}</p>
                    <h1 className='text-3xl font-bold text-center px-10 pt-5 mt-[5px]'>$ {pricing}</h1>
                    <p className='text-[10px]  text-center px-4'>(free delivery included for within US,NG,UK)</p>
                    <div className='w-[50%] flex mx-auto justify-between my-5'>
                        <div><button>See More!</button></div>

                        <div>
                            {cartIcon ? <button onClick={() => { addToCart(newItem); switchCartButton() }}>Add to Cart</button> :
                                <button onClick={() => { removeFromCart(id); switchCartButton() }}> Take off Cart</button> 
                            }
                        </div>
                        </div>

                </div>
            </div></div>
  )
}
