import { initializeApp } from "firebase/app";
import { GoogleAuthProvider } from "firebase/auth";
import { getAuth, signInWithPopup } from "firebase/auth";

const firebaseConfig = {
    apiKey: "AIzaSyB2xVC4jyt_qwrBhuj6wuuGjA1-RWLCm_0",
    authDomain: "gentlemen-commerce.firebaseapp.com",
    projectId: "gentlemen-commerce",
    storageBucket: "gentlemen-commerce.appspot.com",
    messagingSenderId: "917995816539",
    appId: "1:917995816539:web:d0af6d769fd7813a7ae17e"
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
const auth = getAuth(app)
const provider = new GoogleAuthProvider();

export const signInWithGoogle = () => {
signInWithPopup(auth, provider)
        .then((result) => {
            const name = result.user.displayName
            const email = result.user.email
            const userPhotoUrl = result.user.photoURL

            localStorage.setItem("name", name)
            localStorage.setItem("email", email)
            localStorage.setItem("userPhoto",userPhotoUrl)
        console.log(result)
        }).catch((error) => {
            console.log(error)
        })
}
