import React from 'react'
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";
import { useContext } from "react";
import CommerceContext from "./context/commerce-context";

export default function HeroProducts({ heroItems }) {
    const { addToCart } = useContext(CommerceContext)
        const settings = {
            infinite: true,
            dots: true,
            slidesToShow: 2,
            slidesToScroll: 2,

            autoplay: true,
            autoplaySpeed: 3000,
        };

        return (
            <>

                <div className="className='md:w-[80%] mx-auto w-[90%] md:h-[550px] h-[350px]">
                    <Slider {...settings}>
                        {heroItems.map((item) => (

                               <div key={item.id}>
                                <img src={item.image} alt={item.alt} className='md:w-[600px] md:h-[500px] object-cover w-[300px] h-[300px]'  />
                                <div className='relative md:top-[-450px] top-[-270px] w-[200px] flex h-[40px] ml-4 '>
                                    <div className='bg-blue-900 '><h1 className='p-3 text-[10px]  font-bold text-white'>NEW ARRIVAL</h1></div>
                                    <div className='bg-slate-800 '><h1 className='p-3 text-[10px]  font-bold text-white'>SHOP NOW</h1></div>
                                </div>
                                <div className='relative md:top-[-100px] top-[-70px] w-[200px] flex h-[40px] ml-4 '>
                                    <div className='bg-blue-900 '><h1 className='p-3 text-[10px]  font-bold text-white'>{item.price}</h1></div>
                                    <div className='bg-slate-800 '><h1 onClick={() => addToCart(item)} className='p-3 text-[10px]  font-bold text-white'>ADD TO CART</h1></div>
                                </div>
                            </div>
                        ))}
                    </Slider>
                </div>
            </>
        )
    }
