import React from "react";
import { ADD_T0_CART, REMOVE_FROM_CART, INC_QTY, DEC_QTY, } from "./commerce-action";
import { useReducer } from "react";
import CommerceContext from "./commerce-context";
import {commerceReducer} from './commerce-reducer'
import { useEffect } from "react";




const InitialState = {
    basket: [], //id, title,price,image
}


// add to cart



export default function CommerceState(props) {
    const [state, dispatch] = useReducer(commerceReducer, InitialState)


    const incQty = (itemID) => {
        dispatch({
            type: INC_QTY,
            payload: itemID
        })
    }

    useEffect(() => {
        localStorage.setItem("localCart", JSON.stringify(state));
    }, [state]);

    const decQty = (itemID) => {
        dispatch({
            type: DEC_QTY,
            payload:itemID

        })
    }
    const addToCart = (item) => {
        dispatch({
            type: ADD_T0_CART,
            payload: item

        })
    }

    const removeFromCart = (itemId) => {
        dispatch({
            type: REMOVE_FROM_CART,
            payload: itemId
        })
    }

  return (

      <CommerceContext.Provider value={{
          basket: state.basket,
          addToCart,
          removeFromCart,
          incQty,
          decQty
      }}>
          {props.children}
      </CommerceContext.Provider>



  )
}
