import './index.css';
import TopNav from './TopNav'
import { useState } from 'react'
import productItems from './productApi';
import { useNavigate, Route, Routes, useLocation } from 'react-router-dom'

function SearchResult() {
    const [searchBox, setSearchBox] = useState("")

    const location = useLocation()
    const searchValue = location.state
    console.log(location.state)

    const searchData = productItems.filter((item) => (
        item.title.toLowerCase().includes(searchValue.toLowerCase()) ||
        item.desc.toLowerCase().includes(searchBox.toLowerCase())
    ))
    console.log(searchData)
    const handleChange = (event) => {

        setSearchBox(event.target.value)

    }
    return (
        <div className='w-full p-4'>





            <input className='w-[300px] h-[30px] bg-slate-200' name='search' onChange={handleChange} />
            <div className='grid grid-cols-5'>

                {myData.map((myData) => (

                    <div>
                        <img src={myData.image} alt={myData.alt} className="w-[100px] h-[100px] object-cover" />
                        <h1>{myData.title}</h1>
                        <h1>{myData.pricing}</h1>
                    </div>))}
            </div>
        </div>

    );
}

export default SearchResult;
