import React from 'react'
import Product from './Product'
import productItems from "./productApi";
import Hottest from './hottest'
import hottest from './hottestApi'

function Home() {

    return (
        <div>
            <div className="className='md:w-[80%] mx-auto w-[90%] md:h-[300px] h-[100px]">

                <div class=" md:flex justify-between w-full">
                    <div class="bg-white w-full md:w-[200px] h-ful pl-3 mx-auto"><h1 className=' text-center text-[30px] p-6 font-bold text-blue-400'> HOTTEST DEALS OF THE DAY</h1> </div>
                    </div>
                    {hottest.slice(0, 3).map(({
                        id,
                        title,
                        image,
                        pricing,
                        alt,
                        qty,
                    }) => (
                        <Hottest
                            key={id}
                            title={title}
                            pricing={pricing}
                            qty={qty}
                            image={image}
                            alt ={alt}
                        />
                   )) }
                </div>

                    <div >
                        {productItems.map((item) => (
                            <Product

                            key ={item.id}
                            title={item.title}
                            pricing={item.pricing}
                            qty ={item.qty}
                            image={item.image}
                       />)) }


                </div>






       </div>

    )
}

export default Home
