import React from 'react'

import {useContext} from 'react'
import CommerceContext from './context/commerce-context'
import ReactImageZoom from 'react-image-zoom';
import { useState } from 'react';

function CheckoutProduct({ key, id, title, image, pricing, clickToRemove, qty, size }) {

    const props = { width: 350, height: 300, zoomWidth: 300, img: image, zoomPosition: "original" };
    const { incQty, decQty } = useContext(CommerceContext)
    const [shoeSize, setShoeSize] = useState(size)
console.log(size)
    function increment() {
        if (qty >= 1) {
            incQty(id)
        }
    }

    function decrement() {
        if (qty > 1) {
            decQty(id)
        }
        else {
            alert ("Quantity of product can not be less that 1")
        }
    }

    return (


        <div className=' md:flex justify-between shadow-2xl p-7' >



            <div className='md:w-[50%] w-[300px] mx-auto'> <ReactImageZoom {...props} /></div>
            <div className='md:w-[50%]  px-1 items-center w-[300px] mx-auto'>
                <h1 className='md:text-xl text-lg md:py-2 py-0 font-bold text-blue-500'>{title}</h1>
                <h1 className='text-sm  py-2 font-semibold'> Price:  ${pricing * qty} </h1>
                <div className='flex justify-start' ><h1 className='text-sm font-semibold'>Quantity:&nbsp; </h1> 
                    <div className='bg-slate-100'> <button className=" w-[26px] h-[26px] p-0 bg-blue-500" onClick={decrement}>-</button>
                <span className="px-3">{qty}</span>
                        <button className="w-[26px] h-[26px] p-0 bg-blue-500" onClick={increment}>+</button></div>


                </div>
                <div className='flex justify-start pt-5' ><h1 className='text-sm font-semibold py-2'>Size:&nbsp; </h1>
                    <div className='bg-slate-100 '>
                        <select value={shoeSize} className='w-[170px] p-2 h-[40px] bg-slate-100 text-xs' onChange={(e) =>setShoeSize(e.target.value)}>
                            <option value={shoeSize}>{shoeSize}</option>
                            <option value={size.L}>{size.L}</option>
                            <option value={size.LM}>{size.LM}</option>
                            <option value={size.XL}>{size.XL}</option>
                            <option value={size.XL2}>{size.XL2}</option>
                            <option value={size.XXL}>{size.XXL}</option>

                        </select>


                    </div>


                </div>
                <div className='pt-4 ' ><button onClick={clickToRemove} className='w-full'>Remove from Basket</button></div>

         </div>







        </div>
    )

}
export default CheckoutProduct
