import React,{useState} from 'react'
import productItems from './productApi'
import { Icon } from '@iconify/react'
import SearchResult from  './SearchResult'
function SearchComponent() {
    const [searchField, setSearchField] = useState("");

    const filteredItems = productItems.find(searchItem => {
        return (
            searchItem
                .title
                .toLowerCase()
                .includes(searchField.toLowerCase()) ||

            searchItem
                .desc
                .toLowerCase()
                .includes(searchField.toLowerCase())
        );
    }
    );
    const handleChange = (e) => {
        setSearchField(e.target.value);
    };
    console.log(filteredItems)
    return (
      <>

            <form>   <div className='hidden md:flex md:w-full h-full'>
              <input type='text' className='md:w-full text-[13px] bg-blue-200 p-4  rounded-full outline-none md:rounded-full 
                        focus:border-[0px] md:box-border box-border md:border-[0px] 
                        h-full' placeholder='Search...' value={searchField} onChange={handleChange} />
      </div>
                <div className='md:bg-blue-200 rounded-full w-17 h-full hidden md:flex'><Link to='/SearchResult'><Icon icon="bxs:search" className='md:w-10 md:h-[full] w-7 h-[30px] text-blue-800 mx-auto my-auto' /></Link></div>
</form>  

            <div className='md:grid md:grid-cols-4 w-full p-4'>
                {
                    filteredItems.map((item) => {
                        <SearchResult 
                            title={item.title}
                            image={item.image}
                            alt={item.alt}
                            desc={item.desc}
                            pricing={item.prizing}
                            size={item.size}
                           />
                    })
                }


            </div>
        </>
    )


}

export default SearchComponent
