import './index.css';
import { useState } from 'react'
import productItems from './productApi';
import { Link, useLocation } from 'react-router-dom'

function SearchResult() {


    const location = useLocation()
    const searchValue = location.state
    console.log(searchValue)

    const searchData = productItems.filter((item) => (
        item.title.toLowerCase().includes(searchValue.toLowerCase()) 
    ))
    console.log(searchData)

    return (
        <div className='w-full p-4'>




            <div className='grid grid-cols-5'>

                {searchData.map(({
                    id,
                    image,
                    title,
                    alt,
                    pricing
                }) => (

                    <div className='bg-slate-200 shadow-2xl'>
                        <img src={image} alt={alt} className="w-[100px] h-[100px] object-cover" />
                        <Link to={`/ItemDisplay/${id}`}> <h1>${title}</h1></Link>
                        <h1>{pricing}</h1>
                    </div>))}
            </div>
        </div>

    );
}

export default SearchResult;
