import React from 'react'
import { useContext } from "react";
import CommerceContext from "./context/commerce-context";
import { toast } from 'react-toastify';
import { Icon } from '@iconify/react';
import Slide from 'react-reveal/Slide'
import {Link} from 'react-router-dom'

function Product({ key, title, pricing, image, qty,alt,id}) {
    const { addToCart, } = useContext(CommerceContext)




    const newItem = {
        id,
        title,
        pricing,
        qty,
        image,
        alt

    }



    const handleAddToCart = () => {
        addToCart(newItem)
        toast("Product has been added to your cart")
    }



    return (
        <div >
<Slide bottom>
                <div className="w-full  mb-6 products " key={id} > <Link to={`/ItemDisplay/${id}`}><img src={image} alt={alt} /></Link>
                    <div className="w-full h-[35px] px-3 "> <Link to={`/ItemDisplay/${id}`}><h1 className=' text-[10px]'>{title}</h1></Link></div>

                <div className='flex justify-start '>
                    <div className='w-[30%] md:w-[50%] pl-[10px] bg-blue-400 mr-auto'></div>
                    <div className=" h-[30px] px-3 p-1 "> <h1 className=' md:text-[16px] text-[12px] font-bold'>$ {pricing}</h1></div>
                    <div className=" h-[30px] px-3 p-1"> <Icon icon="bi:cart-plus-fill" onClick={ handleAddToCart} className='text-2xl  text-blue-400' /></div>
                </div>

                <hr />
            </div>
            </Slide>


        </div>



    )
}
export default Product

