import React from 'react'
import {toast } from 'react-toastify';
import { Icon } from '@iconify/react';
import { useState, useContext } from 'react';
import CommerceContext from './context/commerce-context';
import { Link } from 'react-router-dom';

function Hottest({ key, title, pricing, image, qty,alt,id,size }) {
    const [cartIcon, setCartIcon] = useState(false)
    const { addToCart, removeFromCart } = useContext(CommerceContext)
    const switchCartButton = () => setCartIcon(!cartIcon)



    const newItem = {
         id,
        title,
        pricing,
        qty,
      image,
        size,

    }



  const handleAddToCart = () => {
    addToCart(newItem)
    toast("Product has been added to your cart")
  }

  const handleRemoveFromCart = () => {
    removeFromCart(id)
    toast("Product has been removed from your cart")
  }

  return (
      <div >

      <div className=" w-[300px] md:w-full mx-auto mb-6  hot" key={key} ><Link to={`/leatherShoes/${id}`}><img src={image} alt={alt}s /></Link> 
        <div className="w-full h-[35px] px-3 "> <Link to={`/leatherShoes/${id}`}> <h1 className=' text-[10px]'>{title}</h1></Link></div>

                      <div className='flex justify-start '>
                          <div className='w-[50%] pl-[10px] bg-blue-400 mr-auto'></div>
                          <div className=" h-[30px] px-3 p-1 "> <h1 className=' text-[16px] font-bold'>$ {pricing}</h1></div>
                          <div className=" h-[30px] px-3 p-1"> {!cartIcon? <Icon icon="bi:cart-plus-fill" onClick={() => {handleAddToCart(); switchCartButton();
                          }} className='text-2xl  text-blue-400' /> : <Icon icon="bi:cart-x" onClick={() => {handleRemoveFromCart(); switchCartButton();
                          }} className='text-2xl  text-red-600' /> }</div>
                      </div>

                  <hr/>
                  </div>



          </div>



  )
}

export default Hottest
