import React from 'react'
import Product from './Product'
import productItems from "./productApi";
import Hottest from './hottest'
import hottestApi from './hottestApi'
import Slide from 'react-reveal/Slide';
import KingsCollection from './kingsCollection';
import kingsData from './kingscollectiondata';
import {useEffect,useState} from 'react'
function Home() {
    const [randomItem,setRandomItem] = useState([])

 let productObject= Object.values(kingsData)
    let randomProducts = productObject[Math.floor(Math.random() * productObject.length)]
    setRandomItem(randomProducts)
    console.log(randomItem)


    return (


        <div className="className='md:w-[90%] mx-auto w-[90%] md:h-[300px] h-[100px]">

            <div class=" md:flex md:justify-between w-full">
                <Slide right>
                <div class="bg-white w-full md:w-[200px] h-ful pl-3 mx-auto">
                    <h1 className=' text-center text-[30px] p-6 font-bold text-blue-400'> HOTTEST DEALS OF THE DAY</h1> </div>
                    {productObject.map(({
                        id,
                        title,
                        image,
                        pricing,
                        alt,
                        qty,
                    }) => (
                        <Hottest
                            key={id}
                            id={id}
                            title={title}
                            pricing={pricing}
                            qty={qty}
                            image={image}
                            alt ={alt}
                        />
                    ))}
                </Slide>
                </div>

            <div >


                    {kingsData.slice(0, 1).map(({
                        id,
                        title,
                        image,
                        pricing,
                        alt,
                        qty,
                    }) => (
                    <KingsCollection
                    key={id}
                    id={id}
                    title={title}
                    pricing={pricing}
                    qty={qty}
                    image={image}
                    alt={alt}
                    /> ))}

            </div>
       </div>

    )
}

export default Home
