import React from 'react'

import { Icon } from '@iconify/react';
import { useState, useContext } from 'react';
import CommerceContext from './context/commerce-context';

function Hottest({ key, title, pricing, image, qty,alt }) {
    const [cartIcon, setCartIcon] = useState(false)
    const { addToCart, removeFromCart } = useContext(CommerceContext)
    const switchCartButton = () => setCartIcon(!cartIcon)



    const newItem = {
        id: Math.random(),
        title: title,
        pricing: pricing,
        qty: qty,
        image: image,

    }
  /*  let keys = Object.values(hottest)
    let ranProducts = keys[Math.floor(Math.random() * keys.length)]
    console.log(ranProducts)
    console.log(keys.length)
    */
  return (
      <div className="className='md:w-[80%] mx-auto w-[90%] md:h-[300px] h-[100px]">
          <div class=" md:flex justify-between w-full">

                  < div className=" w-[300px] mx-auto  hot" key={key} > <img src={image} alt={alt} />
                      <div className="w-full h-[35px] px-3 "> <h1 className=' text-[10px]'>{title}</h1></div>

                      <div className='flex justify-start '>
                          <div className='w-[50%] pl-[10px] bg-blue-400 mr-auto'></div>
                          <div className=" h-[30px] px-3 p-1 "> <h1 className=' text-[16px] font-bold'>$ {pricing}</h1></div>
                          <div className=" h-[30px] px-3 p-1"> {!cartIcon? <Icon icon="bi:cart-plus-fill" onClick={() => {addToCart(newItem); switchCartButton();
                          }} className='text-2xl  text-blue-400' /> : <Icon icon="bi:cart-x" onClick={() => {
                              removeFromCart(key); switchCartButton();
                          }} className='text-2xl  text-red-600' /> }</div>
                      </div>


                  </div>



          </div>









      </div>
  )
}

export default Hottest
