import React from 'react'
import './Product.css'
import { useContext } from "react";
import CommerceContext from "./context/commerce-context";

function Product({ title, pricing, image }) {
const {addToCart} = useContext (CommerceContext)

   const newItem = {
   id: Math.random(),
   title: title,
   pricing: pricing,
   image: image
 }

  const clickHandler = () => (addToCart(newItem)) 
    return (
        <div>

            <div className="product">
                <div className="product__info">
                    <p>{title}</p>
                    <p className="product__price">
                        <small>$</small>
                        <p>{ pricing}</p>
                        </p>

                </div>
                <img src={image} alt=""/>

                <button onClick={clickHandler}>Add to Basket</button>


            </div>
        </div>
    )
}
export default Product

