import React from 'react'
import './Product.css'
import { useContext } from "react";
import CommerceContext from "./context/commerce-context";
import {useState} from 'react'

function Product({ key, title, pricing, image, }) {
    const { addToCart } = useContext(CommerceContext)
    const [qtyInput,SetQtyInput] = useState(1)

   const newItem = {
   id:Math.random(),
   title: title,
   pricing: qtyInput*pricing,
   qty:"1",
   image: image,

   }

    function onCh(event) {
        SetQtyInput(event.target.value)
    }
    const clickHandler = () => {

        addToCart(newItem)

    }
    return (
        <div>

            <div className="product">
                <div className="product__info">
                    <p>{title}</p>
                    <p className="product__price">
                        <p><small>$</small>{ pricing}</p>
                        </p>

                </div>
                <img src={image} alt=""/>

                <button onClick={clickHandler}>Add to Basket</button>


            </div>
        </div>
    )
}
export default Product

